def reset_activity_processing():
    # Delete stage2/M365/TechActivity if it exists
    files = mssparkutils.fs.ls(s2_M365)
    #print(file.name, file.isDir, file.isFile, file.path, file.size)
    for file in files:
        print(file.name)
        if file.name == 'TechActivity':
            mssparkutils.fs.rm(s2_M365 + '/TechActivity', True)

    files = mssparkutils.fs.ls(s1_M365 + '/activity')
    for file in files:
        print(file.name)
        if file.name == '_checkpoints':
            mssparkutils.fs.rm(s1_M365 + '/activity/_checkpoints', True)
